"use strict";

function _classCallCheck(instance, Constructor) { if (!(instance instanceof Constructor)) { throw new TypeError("Cannot call a class as a function"); } }

var Game = function Game(options) {
    "use strict";

    _classCallCheck(this, Game);

    console.log("instantiated the game");
    console.log(options);
    console.log(document.getElementById("view"));

    this.CTX = options.canvas.getContext('2d');
    var offScreenCanvas = document.createElement('canvas');
    this.bufferCTX = offScreenCanvas.getContext('2d');

    // bufferCTX draw calls
    this.bufferCTX.fillRect(10, 10, 10, 10);

    // write the back buffer to the main canvas
    this.CTX.drawImage(this.bufferCTX, 0, 0);
};
